            Assert.That(Convert.ToHexString(deserializedLightClientBootstrap.CurrentSyncCommitteeBranch[i].AsSpan()).ToLower(), Is.EqualTo(Convert.ToHexString(branch[i].AsSpan()).ToLower()));
        }
